// Creating variables to store user information
var data = [{name: "name", email: "email", classes: "classes"}];
var coordinates = [{latitude: 0.0, longitude: 0.0}];
var test = [];

// Getting info from user metadata and using Geoipigy API to retrieve coordinates from user IP addresses
axios.request(options).then(function (response) {
    test.push(response.data);
    for (let i = 0; i < response.data.length; ++i) {
        fetch(`https://api.geoapify.com/v1/ipinfo?&ip=${response.data[i].last_ip}&apiKey=e1690d731c6a4a7e8bc071f67a045952`, requestOptions)
        .then(response => response.json())
        .then(result => coordinates.push(result.location))
        .then(data.push({name: response.data[i].nickname, email: response.data[i].email, classes: response.data[i].user_metadata.classes}))
        .catch(error => console.log('error', error));
    }

}).catch(function (error) {
    console.error(error);
});

